/* ------------------------------------------------------------------ */
/* MEDIA */
/* ------------------------------------------------------------------ */

@media (max-width: 767px) {  /*A partir de 767 on fais : */
.my-sidebar {
  position: relative; /*Pour que le menu s'affiche bien entre le header et la carte*/
  width: 100%;

}


.menu-content{
display: block; /*Afficher les éléments du menu l'un en dessous de l'autre*/
}

.container{
  width: 100%;

}
#hamburger{
  color: rgb(33, 67, 90);
}

.brand {
  text-align: left !important;  /*Positionner le texte à gauche*/
  font-size: 14px; /*Taille du texte*/
  line-height: 50px !important; /*Hauteur du menu*/
}
}



@media (min-width: 767px) { /*Jusqu'a 767 px on fais : */
#hamburger{
  visibility:hidden;
}

.content{

  left: 0;
  margin-left: 160px; /*Positionnement de la carte et de la légende*/
  min-height: 100%;
  margin-top : 10px;
}

.collapse:not(.show) {
  display: block!important ; /*On oblige l'affichage du contenu du menu */
}

}
@media (min-width: 576px){
