    } else {
      return [128, 128, 128, 1]
    }
  }
  var raster =new TileLayer({
    source: new Stamen({
      layer: 'terrain'
    })
  });
  var source = new VectorSource();
  var foncier2 = new VectorLayer({
    style: function (feature, res) {
      property = feature.get("nature");
      return new  Style({
        stroke: new Stroke({
          color: [40, 40, 40, 1],
          width: 0.3
        }),
        fill: new Fill({
          color: couleur(property)
        })
      })
    },
    source: source,
    
        style: new Style({
          fill: new Fill({
            color: 'rgba(255, 255, 255, 0.2)'
          }),
          stroke: new Stroke({
            color: '#ffcc33',
            width: 2
          }),
        })
  });
  // déclaration de la carte
  var map = new Map({
      layers: [raster,foncier2],
    target: 'map',
    view: new View({
      //  dataProjection: 'EPSG:4326',
      //  featureProjection: 'EPSG:3457',
      // center: [0, 0],
      // zoom: 2
      center: fromLonLat([2, 48]),
      zoom: 6
    }),
  });
  
  //Interaction avec la carte
  map.addEventListener("click", function (evt) {
    map.forEachFeatureAtPixel(evt.pixel, function (feature, layer) { 
    // Création de la liste de nature des parcelles 
      var Nature = ['Zone AU', 'Parcelle bÃ¢tie','Parcelle non bÃ¢tie'];
